import React, { useState, useEffect } from "react";
import { csv } from "d3";
import setsData from "../data/sets.csv";
import themesData from "../data/themes.csv";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import SearchIcon from "@mui/icons-material/Search";
import Button from "@mui/material/Button";
import SearchResults from "./SearchResults";


const SearchInput = () => {
const [sets, setSets] = useState();
  const [themes, setThemes] = useState();
  const [search, setSearch] = useState("");
  const [result, setResult] = useState()
  useEffect(() => {
    csv(setsData).then((data) => {
      setSets(data);
    });
    csv(themesData).then((data) => {
      setThemes(data);
    });
  }, []);

  const handleChange = (event) => {
    setSearch(event.target.value);
  };

  const searchLego = (event) => {
    event.preventDefault();
    let searchIndex = sets.findIndex(row => row.set_id == search)
    let setsResult = sets[searchIndex]
    let themeIndex = themes.findIndex(row => row.id == setsResult.theme_id)
    let themesResult = themes[themeIndex]
    setResult({
        themeName: themesResult.name,
        ...setsResult,
    })
  };
  return (
    <div>SearchInput</div>
  )
}

export default SearchInput
