import React, { useState, useEffect, FormEventHandler, FormEvent } from "react";
import { csv, DSVRowArray } from "d3";
import TextField from "@mui/material/TextField";
import { Search } from "@mui/icons-material";
import Button from "@mui/material/Button";
import SearchResults from "./SearchResults";
import { SearchResult } from "../models/dataModels";
import CircularProgress from '@mui/material/CircularProgress';
import setsData from './sets'
const SearchInput = () => {
  const [sets, setSets] = useState<DSVRowArray>();
  const [themes, setThemes] = useState<DSVRowArray>();
  const [search, setSearch] = useState<string>("");
  const [result, setResult] = useState<SearchResult>();
  const [loading, setLoading] = useState(false);
  useEffect(() => {
    csv(setsData).then((data) => {
      setSets(data);
    });
    csv("../data/themes.csv").then((data) => {
      setThemes(data);
    });
  }, []);

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setSearch(event.target.value);
  };

  const searchLego = (event:FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    // setLoading(true);
    if (sets && themes) {
      let searchIndex = sets.findIndex((row) => row.set_id == search);
      console.log(searchIndex)
      let setsResult = sets[searchIndex];
      console.log(setsResult)
      let themeIndex = themes.findIndex((row) => row.id == setsResult.theme_id);
      let themesResult = themes[themeIndex];
      console.log(themesResult)
      if (themesResult) {
        setResult({
          themeName: themesResult.name,
          set_id: setsResult.set_id,
          name: setsResult.name,
          year: setsResult.year,
          theme_id: setsResult.thene_id,
          num_parts: setsResult.num_parts,
        });
      }
    //   setLoading(false)
    }

  };
  return (
    <div>
      <form onSubmit={searchLego}>
        <TextField
          id="outlined-basic"
          label="Search"
          variant="outlined"
          onChange={handleChange}
        />
        <Button variant="contained" endIcon={<Search />} type="submit">
          Search
        </Button>
      </form>
      {loading?
        (<CircularProgress />):
        result? 
            (<SearchResults searchResults={result}></SearchResults>) :
        ("Error")
      }
    </div>
  );
};

export default SearchInput;
