import React, { useState, useEffect } from "react";
import {csv, DSVRowArray} from "d3";
import setsData from "../data/sets.csv"
import themesData from "../data/themes.csv";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import SearchIcon from "@mui/icons-material/Search";
import Button from "@mui/material/Button";
import SearchResults from "./SearchResults";
import {Row} from "../models/dataModels"

const SearchInput = () => {
  const [sets, setSets] = useState<DSVRowArray>();
  const [themes, setThemes] = useState<DSVRowArray>();
  const [search, setSearch] = useState<string>("");
  const [result, setResult] = useState();
  useEffect(() => {
    csv(setsData).then((data) => {
        console.log(data)
      setSets(data);
    });
    csv(themesData).then((data) => {
      setThemes(data);
    });
  }, []);

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setSearch(event.target.value);
  };

  const searchLego = (event: React.MouseEvent<HTMLButtonElement>) => {
    event.preventDefault();
    if (sets && themes){
        let searchIndex = sets?.findIndex((row) => row.set_id == search);
        let setsResult = sets[searchIndex] 
        let themeIndex = themes.findIndex((row) => row.id == setsResult.theme_id);
        let themesResult = themes[themeIndex];
        setResult({
            themeName: themesResult.name,
            ...setsResult,
        });
    }



    setResult({
      themeName: themesResult.name,
      ...setsResult,
    });
  };
  return <div>SearchInput</div>;
};

export default SearchInput;
